import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import { formatDate } from '../utils/formatters';
import './ExpenseList.css';

const ExpenseList = () => {
  const { user } = useContext(AuthContext);
  const [expenses, setExpenses] = useState([]);
  const [filteredExpenses, setFilteredExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCurrency, setSelectedCurrency] = useState('USD');
  const [exchangeRates, setExchangeRates] = useState({});
  const [sortConfig, setSortConfig] = useState({ key: 'date', direction: 'desc' });
  const [filter, setFilter] = useState({
    category: '',
    dateFrom: '',
    dateTo: '',
    amountMin: '',
    amountMax: '',
    searchTerm: ''
  });
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [expenseToDelete, setExpenseToDelete] = useState(null);
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');

  // Fetch expenses
  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/api/expenses', {
          headers: { Authorization: `Bearer ${user.token}` }
        });
        setExpenses(response.data);
        setFilteredExpenses(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch expenses. Please try again later.');
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [user]);

  // Fetch exchange rates
  useEffect(() => {
    const fetchExchangeRates = async () => {
      try {
        const response = await axios.get('/api/exchange-rates');
        setExchangeRates(response.data.rates);
      } catch (err) {
        console.error('Failed to fetch exchange rates', err);
      }
    };

    fetchExchangeRates();
  }, []);

  // Handle sorting
  const requestSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });

    const sortedExpenses = [...filteredExpenses].sort((a, b) => {
      if (key === 'amount') {
        return direction === 'asc' 
          ? convertAmount(a.amount, a.currency) - convertAmount(b.amount, b.currency)
          : convertAmount(b.amount, b.currency) - convertAmount(a.amount, a.currency);
      }

      if (key === 'date') {
        return direction === 'asc'
          ? new Date(a.date) - new Date(b.date)
          : new Date(b.date) - new Date(a.date);
      }

      if (a[key] < b[key]) return direction === 'asc' ? -1 : 1;
      if (a[key] > b[key]) return direction === 'asc' ? 1 : -1;
      return 0;
    });

    setFilteredExpenses(sortedExpenses);
  };

  // Handle filtering
  useEffect(() => {
    let result = [...expenses];

    if (filter.category) {
      result = result.filter(expense => 
        expense.category.toLowerCase() === filter.category.toLowerCase()
      );
    }

    if (filter.dateFrom) {
      result = result.filter(expense => 
        new Date(expense.date) >= new Date(filter.dateFrom)
      );
    }

    if (filter.dateTo) {
      result = result.filter(expense => 
        new Date(expense.date) <= new Date(filter.dateTo)
      );
    }

    if (filter.amountMin) {
      result = result.filter(expense => 
        convertAmount(expense.amount, expense.currency) >= parseFloat(filter.amountMin)
      );
    }

    if (filter.amountMax) {
      result = result.filter(expense => 
        convertAmount(expense.amount, expense.currency) <= parseFloat(filter.amountMax)
      );
    }

    if (filter.searchTerm) {
      const term = filter.searchTerm.toLowerCase();
      result = result.filter(expense => 
        expense.description.toLowerCase().includes(term) ||
        expense.category.toLowerCase().includes(term)
      );
    }

    setFilteredExpenses(result);
    // Re-apply current sorting
    if (sortConfig.key) {
      requestSort(sortConfig.key);
    }
  }, [filter, expenses]);

  // Currency conversion
  const convertAmount = (amount, fromCurrency) => {
    if (!exchangeRates[fromCurrency] || !exchangeRates[selectedCurrency]) {
      return amount; // Return original if we can't convert
    }

    // Convert to USD first (base currency), then to target currency
    const amountInUSD = fromCurrency === 'USD' ? 
      amount : amount / exchangeRates[fromCurrency];
    return selectedCurrency === 'USD' ? 
      amountInUSD : amountInUSD * exchangeRates[selectedCurrency];
  };

  // Format currency display
  const formatCurrency = (amount, currency) => {
    const convertedAmount = convertAmount(amount, currency);
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: selectedCurrency,
      minimumFractionDigits: 2
    }).format(convertedAmount);
  };

  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter(prev => ({ ...prev, [name]: value }));
  };

  // Reset filters
  const resetFilters = () => {
    setFilter({
      category: '',
      dateFrom: '',
      dateTo: '',
      amountMin: '',
      amountMax: '',
      searchTerm: ''
    });
  };

  // Delete expense
  const deleteExpense = async (id) => {
    if (window.confirm('Are you sure you want to delete this expense?')) {
      try {
        await axios.delete(`/api/expenses/${id}`, {
          headers: { Authorization: `Bearer ${user.token}` }
        });

        setExpenses(prevExpenses => 
          prevExpenses.filter(expense => expense._id !== id)
        );

        setFilteredExpenses(prevFiltered => 
          prevFiltered.filter(expense => expense._id !== id)
        );
      } catch (err) {
        setError('Failed to delete expense. Please try again.');
      }
    }
  };

  // Get unique categories for filter dropdown
  const categories = [...new Set(expenses.map(expense => expense.category))];

  // Calculate totals
  const totalExpenses = filteredExpenses.reduce((sum, expense) => 
    sum + convertAmount(expense.amount, expense.currency), 0);

  const confirmDelete = (id) => {
    setExpenseToDelete(id);
    setShowConfirmModal(true);
  };

  const deleteExpenseConfirmed = () => {
    deleteExpense(expenseToDelete);
    setShowConfirmModal(false);
    setNotificationMessage('Expense deleted successfully');
    setShowNotification(true);
    setTimeout(() => setShowNotification(false), 3000);
  };

  const cancelDelete = () => {
    setExpenseToDelete(null);
    setShowConfirmModal(false);
  };

  if (loading) return <div className="loading">Loading expenses...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="expense-list-container">
      <h2>Your Expenses</h2>

      {showNotification && (
        <div className="alert alert-success">{notificationMessage}</div>
      )}

      <div className="filters-container">
        <div className="filter-row">
          <div className="filter-group">
            <label>Category:</label>
            <select 
              name="category" 
              value={filter.category} 
              onChange={handleFilterChange}
            >
              <option value="">All Categories</option>
              {categories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>From:</label>
            <input 
              type="date" 
              name="dateFrom" 
              value={filter.dateFrom} 
              onChange={handleFilterChange}
            />
          </div>

          <div className="filter-group">
            <label>To:</label>
            <input 
              type="date" 
              name="dateTo" 
              value={filter.dateTo} 
              onChange={handleFilterChange}
            />
          </div>
        </div>

        <div className="filter-row">
          <div className="filter-group">
            <label>Min Amount:</label>
            <input 
              type="number" 
              name="amountMin" 
              value={filter.amountMin} 
              onChange={handleFilterChange}
            />
          </div>

          <div className="filter-group">
            <label>Max Amount:</label>
            <input 
              type="number" 
              name="amountMax" 
              value={filter.amountMax} 
              onChange={handleFilterChange}
            />
          </div>

          <div className="filter-group">
            <label>Search:</label>
            <input 
              type="text" 
              name="searchTerm" 
              value={filter.searchTerm} 
              onChange={handleFilterChange}
              placeholder="Search description or category..."
            />
          </div>

          <button className="reset-btn" onClick={resetFilters}>
            Reset Filters
          </button>
        </div>
      </div>

      <div className="currency-selector">
        <label>Display Currency:</label>
        <select 
          value={selectedCurrency} 
          onChange={(e) => setSelectedCurrency(e.target.value)}
        >
          <option value="USD">USD ($)</option>
          <option value="EUR">EUR (€)</option>
          <option value="GBP">GBP (£)</option>
          <option value="JPY">JPY (¥)</option>
          <option value="CAD">CAD ($)</option>
        </select>
      </div>

      <div className="total-section">
        <h3>
          Total: {new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: selectedCurrency
          }).format(totalExpenses)}
        </h3>
        <p>Showing {filteredExpenses.length} of {expenses.length} expenses</p>
      </div>

      {filteredExpenses.length === 0 ? (
        <div className="no-expenses">
          <p>No expenses found. Add some expenses or adjust your filters.</p>
        </div>
      ) : (
        <div className="table-container">
          <table className="expense-table">
            <thead>
              <tr>
                <th onClick={() => requestSort('date')}>
                  Date
                  {sortConfig.key === 'date' && (
                    <span className={`sort-indicator ${sortConfig.direction}`}>
                      {sortConfig.direction === 'asc' ? '↑' : '↓'}
                    </span>
                  )}
                </th>
                <th onClick={() => requestSort('category')}>
                  Category
                  {sortConfig.key === 'category' && (
                    <span className={`sort-indicator ${sortConfig.direction}`}>
                      {sortConfig.direction === 'asc' ? '↑' : '↓'}
                    </span>
                  )}
                </th>
                <th onClick={() => requestSort('description')}>
                  Description
                  {sortConfig.key === 'description' && (
                    <span className={`sort-indicator ${sortConfig.direction}`}>
                      {sortConfig.direction === 'asc' ? '↑' : '↓'}
                    </span>
                  )}
                </th>
                <th onClick={() => requestSort('amount')}>
                  Amount
                  {sortConfig.key === 'amount' && (
                    <span className={`sort-indicator ${sortConfig.direction}`}>
                      {sortConfig.direction === 'asc' ? '↑' : '↓'}
                    </span>
                  )}
                </th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredExpenses.map(expense => (
                <tr key={expense._id}>
                  <td>{formatDate(expense.date)}</td>
                  <td>
                    <span className={`category-badge ${expense.category.toLowerCase()}`}>
                      {expense.category}
                    </span>
                  </td>
                  <td>{expense.description}</td>
                  <td className="amount-cell">
                    <span className="original-amount">
                      {new Intl.NumberFormat('en-US', {
                        style: 'currency',
                        currency: expense.currency
                      }).format(expense.amount)}
                    </span>
                    {expense.currency !== selectedCurrency && (
                      <span className="converted-amount">
                        ({formatCurrency(expense.amount, expense.currency)})
                      </span>
                    )}
                  </td>
                  <td>
                    <button 
                      className="edit-btn" 
                      onClick={() => window.location.href = `/edit-expense/${expense._id}`}
                    >
                      <i className="fas fa-edit"></i>
                    </button>
                    <button 
                      className="delete-btn" 
                      onClick={() => confirmDelete(expense._id)}
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Confirm Delete Modal */}
      {showConfirmModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Confirm Delete</h3>
            <p>Are you sure you want to delete this expense?</p>
            <div className="modal-actions">
              <button 
                className="btn btn-danger" 
                onClick={deleteExpenseConfirmed}
              >
                Delete
              </button>
              <button 
                className="btn" 
                onClick={cancelDelete}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpenseList;
