import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddExpense = () => {
  const [formData, setFormData] = useState({
    amount: '',
    category: '',
    description: '',
    date: '',
  });
  const navigate = useNavigate();

  const { amount, category, description, date } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
        },
      };

      const res = await axios.post('/api/expenses', formData, config);
      navigate('/expenses');
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="add-expense">
      <h2>Add New Expense</h2>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label>Amount</label>
          <input
            type="number"
            step="0.01"
            name="amount"
            value={amount}
            onChange={onChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Category</label>
          <select name="category" value={category} onChange={onChange} required>
            <option value="">Select a category</option>
            <option value="Food">Food</option>
            <option value="Transportation">Transportation</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Education">Education</option>
            <option value="Shopping">Shopping</option>
            <option value="Bills">Bills</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div className="form-group">
          <label>Description</label>
          <input
            type="text"
            name="description"
            value={description}
            onChange={onChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Date</label>
          <input
            type="date"
            name="date"
            value={date}
            onChange={onChange}
            required
          />
        </div>
        <input type="submit" value="Add Expense" className="btn btn-primary" />
      </form>
    </div>
  );
};

export default AddExpense;
