import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const ExpenseList = () => {
  const [expenses, setExpenses] = useState([]);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('date');
  const [sortDirection, setSortDirection] = useState('desc');
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [expenseToDelete, setExpenseToDelete] = useState(null);
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');

  useEffect(() => {
    // Mock expenses data
    const mockExpenses = [
      {
        _id: '1',
        amount: 150,
        category: 'Food',
        description: 'Grocery shopping',
        date: new Date('2023-08-15')
      },
      {
        _id: '2',
        amount: 50,
        category: 'Transportation',
        description: 'Gas',
        date: new Date('2023-08-14')
      },
      {
        _id: '3',
        amount: 200,
        category: 'Education',
        description: 'Books',
        date: new Date('2023-08-10')
      },
      {
        _id: '4',
        amount: 100,
        category: 'Entertainment',
        description: 'Movie night',
        date: new Date('2023-08-08')
      },
      {
        _id: '5',
        amount: 75,
        category: 'Shopping',
        description: 'Clothes',
        date: new Date('2023-08-05')
      },
      {
        _id: '6',
        amount: 120,
        category: 'Food',
        description: 'Dining out',
        date: new Date('2023-08-03')
      },
      {
        _id: '7',
        amount: 60,
        category: 'Bills',
        description: 'Internet bill',
        date: new Date('2023-08-01')
      }
    ];

    setExpenses(mockExpenses);
  }, []);

  const confirmDelete = (id) => {
    setExpenseToDelete(id);
    setShowConfirmModal(true);
  };

  const deleteExpense = () => {
    setExpenses(expenses.filter((expense) => expense._id !== expenseToDelete));
    setShowConfirmModal(false);
    setNotificationMessage('Expense deleted successfully');
    setShowNotification(true);
    setTimeout(() => setShowNotification(false), 3000);
  };

  const cancelDelete = () => {
    setExpenseToDelete(null);
    setShowConfirmModal(false);
  };

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortDirection('asc');
    }
  };

  const filteredExpenses = expenses.filter((expense) => {
    const matchesFilter =
      filter === 'all' || expense.category.toLowerCase() === filter.toLowerCase();
    const matchesSearch = expense.description
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    return matchesFilter && matchesSearch;
  });

  const sortedExpenses = [...filteredExpenses].sort((a, b) => {
    if (sortBy === 'date') {
      return sortDirection === 'asc'
        ? new Date(a.date) - new Date(b.date)
        : new Date(b.date) - new Date(a.date);
    } else if (sortBy === 'amount') {
      return sortDirection === 'asc'
        ? a.amount - b.amount
        : b.amount - a.amount;
    } else if (sortBy === 'category') {
      return sortDirection === 'asc'
        ? a.category.localeCompare(b.category)
        : b.category.localeCompare(a.category);
    } else if (sortBy === 'description') {
      return sortDirection === 'asc'
        ? a.description.localeCompare(b.description)
        : b.description.localeCompare(a.description);
    }
    return 0;
  });

  const getSortIcon = (column) => {
    if (sortBy !== column) return null;
    return sortDirection === 'asc' ? '↑' : '↓';
  };

  // Calculate total amount for filtered expenses
  const totalAmount = filteredExpenses.reduce((total, expense) => total + expense.amount, 0);

  return (
    <div className="expense-list">
      <h2>Expenses</h2>

      {showNotification && (
        <div className="alert alert-success">{notificationMessage}</div>
      )}

      <div className="filters">
        <div className="search">
          <input
            type="text"
            placeholder="Search expenses..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="category-filter">
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="all">All Categories</option>
            <option value="Food">Food</option>
            <option value="Transportation">Transportation</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Education">Education</option>
            <option value="Shopping">Shopping</option>
            <option value="Bills">Bills</option>
            <option value="Other">Other</option>
          </select>
        </div>
      </div>

      <div className="expense-actions">
        <Link to="/add-expense" className="btn btn-primary">
          <i className="icon-plus"></i> Add New Expense
        </Link>

        <div className="expense-summary">
          <span className="summary-text">
            Showing {filteredExpenses.length} of {expenses.length} expenses
          </span>
          <span className="summary-amount">
            Total: <strong>₹{totalAmount.toFixed(2)}</strong>
          </span>
        </div>
      </div>

      {filteredExpenses.length === 0 ? (
        <div className="no-expenses">
          <p>No expenses found</p>
          <p>Try adjusting your filters or add a new expense</p>
        </div>
      ) : (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th onClick={() => handleSort('date')} className="sortable-header">
                  Date {getSortIcon('date')}
                </th>
                <th onClick={() => handleSort('category')} className="sortable-header">
                  Category {getSortIcon('category')}
                </th>
                <th onClick={() => handleSort('description')} className="sortable-header">
                  Description {getSortIcon('description')}
                </th>
                <th onClick={() => handleSort('amount')} className="sortable-header">
                  Amount {getSortIcon('amount')}
                </th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {sortedExpenses.map((expense) => (
                <tr key={expense._id}>
                  <td>{new Date(expense.date).toLocaleDateString()}</td>
                  <td>
                    <span 
                      className="category" 
                      data-category={expense.category}
                    >
                      {expense.category}
                    </span>
                  </td>
                  <td>{expense.description}</td>
                  <td className="amount">₹{expense.amount.toFixed(2)}</td>
                  <td>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => confirmDelete(expense._id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Confirm Delete Modal */}
      {showConfirmModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Confirm Delete</h3>
            <p>Are you sure you want to delete this expense?</p>
            <div className="modal-actions">
              <button 
                className="btn btn-danger" 
                onClick={deleteExpense}
              >
                Delete
              </button>
              <button 
                className="btn" 
                onClick={cancelDelete}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpenseList;
