import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const [expenses, setExpenses] = useState([]);
  const [totalSpent, setTotalSpent] = useState(0);
  const [monthlyBudget, setMonthlyBudget] = useState(1000);
  const [isEditingBudget, setIsEditingBudget] = useState(false);
  const [newBudget, setNewBudget] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [netWorth, setNetWorth] = useState(64283.02);
  const [accounts, setAccounts] = useState({
    cash: [
      { name: "Alice's wallet", balance: 250.00, currency: 'USD' },
      { name: "Alice's wallet", balance: 0.00, currency: 'EUR' },
      { name: "Alice's wallet", balance: 0, currency: 'JPY' },
      { name: "Bob's wallet", balance: 267.15, currency: 'USD' },
      { name: "Bob's wallet", balance: 0.00, currency: 'EUR' },
      { name: "Bob's wallet", balance: 12500, currency: 'JPY' },
    ],
    bank: [
      { name: "Checking", balance: 62995.00, currency: 'USD' },
      { name: "Checking", balance: 0.00, currency: 'EUR' },
