import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const [expenses, setExpenses] = useState([]);
  const [totalSpent, setTotalSpent] = useState(0);
  const [monthlyBudget, setMonthlyBudget] = useState(1000);
  const [isEditingBudget, setIsEditingBudget] = useState(false);
  const [newBudget, setNewBudget] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const { user } = useContext(AuthContext);

  useEffect(() => {
    // Mock expenses data
    const mockExpenses = [
      {
        _id: '1',
        amount: 150,
        category: 'Food',
        description: 'Grocery shopping',
        date: new Date('2023-08-15')
      },
      {
        _id: '2',
        amount: 50,
        category: 'Transportation',
        description: 'Gas',
        date: new Date('2023-08-14')
      },
      {
        _id: '3',
        amount: 200,
        category: 'Education',
        description: 'Books',
        date: new Date('2023-08-10')
      },
      {
        _id: '4',
        amount: 100,
        category: 'Entertainment',
        description: 'Movie night',
        date: new Date('2023-08-08')
      },
      {
        _id: '5',
        amount: 75,
        category: 'Shopping',
        description: 'Clothes',
        date: new Date('2023-08-05')
      }
    ];

    setExpenses(mockExpenses);

    // Calculate total spent
    const total = mockExpenses.reduce((acc, expense) => acc + expense.amount, 0);
    setTotalSpent(total);

    // Set monthly budget from user if available
    if (user && user.monthlyBudget) {
      setMonthlyBudget(user.monthlyBudget);
    }
  }, [user]);

  const handleBudgetEdit = () => {
    setIsEditingBudget(true);
    setNewBudget(monthlyBudget);
  };

  const handleBudgetSubmit = (e) => {
    e.preventDefault();
    const budget = parseFloat(newBudget);
    if (isNaN(budget) || budget <= 0) {
      setAlertMessage('Please enter a valid budget amount');
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 3000);
      return;
    }
    setMonthlyBudget(budget);
    setIsEditingBudget(false);
    setAlertMessage('Budget updated successfully!');
    setShowAlert(true);
    setTimeout(() => setShowAlert(false), 3000);
  };

  const remainingBudget = monthlyBudget - totalSpent;
  const percentSpent = (totalSpent / monthlyBudget) * 100;

  return (
    <div className="dashboard">
      <h2>Dashboard</h2>

      {showAlert && (
        <div className={`alert ${alertMessage.includes('successfully') ? 'alert-success' : 'alert-danger'}`}>
          {alertMessage}
        </div>
      )}

      <div className="summary-cards">
        <div className="card">
          <h3>Monthly Budget</h3>
          {isEditingBudget ? (
            <form onSubmit={handleBudgetSubmit}>
              <div className="form-group">
                <input
                  type="number"
                  value={newBudget}
                  onChange={(e) => setNewBudget(e.target.value)}
                  step="0.01"
                  min="0"
                  required
                />
              </div>
              <div className="form-actions">
                <button type="submit" className="btn btn-primary">Save</button>
                <button 
                  type="button" 
                  className="btn btn-danger"
                  onClick={() => setIsEditingBudget(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          ) : (
            <>
              <p>${monthlyBudget}</p>
              <button 
                onClick={handleBudgetEdit} 
                className="btn btn-primary btn-sm"
              >
                Edit Budget
              </button>
            </>
          )}
        </div>
        <div className="card">
          <h3>Total Spent</h3>
          <p>${totalSpent}</p>
          <div className="progress-container">
            <div 
              className="progress-bar"
              style={{ 
                width: `${Math.min(percentSpent, 100)}%`,
                backgroundColor: percentSpent > 80 ? '#e74c3c' : percentSpent > 60 ? '#f39c12' : '#2ecc71'
              }}
            ></div>
          </div>
          <p className="progress-text">{Math.round(percentSpent)}% of budget</p>
        </div>
        <div className="card">
          <h3>Remaining</h3>
          <p className={remainingBudget < 0 ? 'negative' : 'positive'}>
            ${remainingBudget.toFixed(2)}
          </p>
          {remainingBudget < 0 && (
            <p className="warning-text">You've exceeded your budget!</p>
          )}
        </div>
      </div>

      <div className="recent-expenses">
        <div className="recent-expenses-header">
          <h3>Recent Expenses</h3>
          <Link to="/add-expense" className="btn btn-primary">
            Add New Expense
          </Link>
        </div>
        {expenses.length === 0 ? (
          <p>No expenses recorded yet</p>
        ) : (
          <ul>
            {expenses.slice(0, 5).map((expense) => (
              <li key={expense._id} className="expense-item">
                <span className="amount">${expense.amount}</span>
                <span className="category">{expense.category}</span>
                <span className="description">{expense.description}</span>
                <span className="date">
                  {new Date(expense.date).toLocaleDateString()}
                </span>
              </li>
            ))}
          </ul>
        )}
        <Link to="/expenses" className="btn">
          View All Expenses
        </Link>
      </div>
    </div>
  );
};

export default Dashboard;
